mapper.update:
update t_dept
set is_delete = 1
where id = #{id}

mapper.select:
select t.id, t.dept_name AS deptName, t.use_status AS useStatus, t.create_time AS createTime from t_dept t where t.is_delete = 0 and t.company = #{company}
and trim(replace(t.dept_name,' ','')) like trim(replace(CONCAT('%',#{searchKey},'%'),' ',''))
ORDER BY t.create_time DESC
